import VideoPlayer from './VideoPlayer';
import { Layout, Typography } from 'antd';

const { Header, Footer, Content } = Layout;
const { Title } = Typography;

const App = () => {
    const playbackUrl = "https://3d26876b73d7.us-west-2.playback.live-video.net/api/video/v1/us-west-2.913157848533.channel.rkCBS9iD1eyd.m3u8";

    return (
        <Layout style={{ minHeight: '100vh', background: '#f0f2f5' }}>
            <Header
                style={{
                    background: '#001529',
                    padding: '10px 20px',
                    color: 'white',
                    textAlign: 'center',
                }}
            >
                <Title level={2} style={{ color: 'white', margin: 0 }}>
                    Live Stream Platform
                </Title>
            </Header>
            <Content style={{ padding: '20px' }}>
                <VideoPlayer playbackUrl={playbackUrl} />
            </Content>
            <Footer style={{ textAlign: 'center' }}>
                <Typography.Text type="secondary">Â© 2024 Live Streaming Platform. All Rights Reserved.</Typography.Text>
            </Footer>
        </Layout>
    );
};

export default App;
